var jsTactileScreenEvents=function(){};jsTactileScreenEvents.prototype={disable:function(e){if(e==="highlight"){this.injectNoHighlightingRule()}else{document.body.addEventListener(""+e+"",this.handlerFunction.bind(this),false)}},injectNoHighlightingRule:function(){var e=document.createElement("style");e.innerHTML="body { -moz-user-select: none; -khtml-user-select: none; -webkit-user-select: none; user-select: none; }";document.body.appendChild(e)},handlerFunction:function(e){e.preventDefault();e.stopPropagation();return false}}